graphql.query:
mutation ($payload:CommonCreateResourceInput!)
{
	commonCreateResource (payload:$payload)
	{
		id
		rowId
		code
		name
		isActive
		hasAttachments
	}
}

query ($query:QueryStatement $constraint:QueryStatement)
{
	commonPaginateResources (query:$query constraint:$constraint)
	{
		total
		count
		rows
	}
}
